local partone_current_floor = 0
for i = 1, #puzzle_input do
    local selected_character = string.sub(puzzle_input, i, i)

    if selected_character == "(" then
        partone_current_floor = partone_current_floor + 1
    else
        partone_current_floor = partone_current_floor - 1
    end
end
print(partone_current_floor)

-- part two
local parttwo_current_floor = 0
local basement_level = -1
local basement_entry_position = 0

for i = 1, #puzzle_input do
    local selected_character = string.sub(puzzle_input, i, i)

    if selected_character == "(" then
        parttwo_current_floor = parttwo_current_floor + 1
    else
        parttwo_current_floor = parttwo_current_floor - 1
    end
    if parttwo_current_floor == basement_level then
        basement_entry_position = i
        break
    end
end
print(basement_entry_position)